mapper.insert:
insert into userBase (
	userId,
	userPwd,
	userName
) values (
	#{userId},
	#{userPwd},
	#{userName}
)

insert into userDetail(
	userId

	, birth


	, model


	, favorite

) values (
	#{userId}

	, #{birth}


	, #{model}


	, #{favorite}

)

mapper.update:
update userBase set userId = #{userId}

	, userPwd=#{userPwd}


	, userName=#{userName}

where userId = #{userId}

update userDetail set userId = #{userId}

		, birth=#{birth}


		, favorite=#{favorite}


		, model=#{model}

	where userId = #{userId}
